{{begin-axiom}}
{{axiom | n = BA_2 \ 0
        | lc= Closure:
        | q = \forall a, b \in S
        | m = a \vee b \in S
}}
{{axiom | m = a \wedge b \in S
}}
{{axiom | m = \neg a \in S
}}
{{axiom | n = BA_2 \ 1
        | lc= Commutativity:
        | q = \forall a, b \in S
        | m = a \vee b = b \vee a
}}
{{axiom | m = a \wedge b = b \wedge a
}}
{{axiom | n = BA_2 \ 2
        | lc= Associativity:
        | q = \forall a, b, c \in S
        | m = a \vee \paren {b \vee c} = \paren {a \vee b} \vee c
}}
{{axiom | m = a \wedge \paren {b \wedge c} = \paren {a \wedge b} \wedge c
}}
{{axiom | n = BA_2 \ 3
        | lc= Absorption Laws:
        | q = \forall a, b \in S
        | m = \paren {a \wedge b} \vee b = b
}}
{{axiom | m = \paren {a \vee b} \wedge b = b
}}
{{axiom | n = BA_2 \ 4
        | lc= Distributivity:
        | q = \forall a, b, c \in S
        | m = a \wedge \paren {b \vee c} = \paren {a \wedge b} \vee \paren {a \wedge c}
}}
{{axiom | m = a \vee \paren {b \wedge c} = \paren {a \vee b} \wedge \paren {a \vee c}
}}
{{axiom | n = BA_2 \ 5
        | lc= Identity Elements:
        | q = \forall a, b \in S
        | m = \paren {a \wedge \neg a} \vee b = b
}}
{{axiom | m = \paren {a \vee \neg a} \wedge b = b
}}
{{end-axiom}}

